import React, { useState } from 'react';
import { Form, Input, Button, notification } from 'antd';
import { Task } from '../types/TaskTypes';
import { createTask } from '../api/taskApi';

const TaskForm: React.FC = () => {
  const [taskName, setTaskName] = useState('');
  const [taskCommand, setTaskCommand] = useState('');
  const [taskOwner, setTaskOwner] = useState('');
  
  const [taskExecutions, setTaskExecutions] = useState([]);

  const handleCreateTask = async () => {
    const taskData: Task = {
      id: 'temp-id',  // Generate the ID server-side, don't use it client-side
      name: taskName,
      command: taskCommand,
      owner: taskOwner,  // Owner is a required field
      taskExecutions: taskExecutions, // taskExecutions is required and passed as an empty array
    };

    try {
      const response = await createTask(taskData);

      if (response) {
        notification.success({
          message: 'Task Created Successfully',
        });
      } else {
        notification.error({
          message: 'Error Creating Task',
        });
      }
    } catch (error: unknown) {  // Specify the type as 'unknown'
      if (error instanceof Error) {
        notification.error({
          message: 'Error Creating Task',
          description: error.message,  // Now TypeScript knows `error` has a `message` property
        });
      } else {
        notification.error({
          message: 'Unknown Error',
          description: 'An unknown error occurred.',
        });
      }
    }
  };

  return (
    <Form style={{ maxWidth: '400px', margin: '0 auto' }}>
      <Form.Item label="Task Name" required>
        <Input
          value={taskName}
          onChange={(e) => setTaskName(e.target.value)}
          placeholder="Enter Task Name"
        />
      </Form.Item>

      <Form.Item label="Task Command" required>
        <Input
          value={taskCommand}
          onChange={(e) => setTaskCommand(e.target.value)}
          placeholder="Enter Command"
        />
      </Form.Item>

      <Form.Item label="Task Owner" required>
        <Input
          value={taskOwner}
          onChange={(e) => setTaskOwner(e.target.value)}
          placeholder="Enter Task Owner"
        />
      </Form.Item>

      <Form.Item>
        <Button type="primary" onClick={handleCreateTask}>
          Create Task
        </Button>
      </Form.Item>
    </Form>
  );
};

export default TaskForm;
